const { SecretVaultWrapper } = require('secretvaults');

const nodes = [
  {
    url: 'https://nildb-nx8v.nillion.network',
    did: 'did:nil:testnet:nillion1qfrl8nje3nvwh6cryj63mz2y6gsdptvn07nx8v',
  },
  {
    url: 'https://nildb-p3mx.nillion.network',
    did: 'did:nil:testnet:nillion1uak7fgsp69kzfhdd6lfqv69fnzh3lprg2mp3mx',
  },
  {
    url: 'https://nildb-rugk.nillion.network',
    did: 'did:nil:testnet:nillion1kfremrp2mryxrynx66etjl8s7wazxc3rssrugk',
  },
];

console.log('NILLION_ORG_DID:', $env.NILLION_ORG_DID);
console.log('NILLION_ORG_SECRET_KEY:', $env.NILLION_ORG_SECRET_KEY);

const collection = new SecretVaultWrapper(
  nodes,
  {
    secretKey: $env.NILLION_ORG_SECRET_KEY,
    orgDid: $env.NILLION_ORG_DID,
  },
  $env.NILLION_VAULT_SCHEMA_ID
);
await collection.init();

const newSchema = await collection.createSchema(
  {
    "$schema": "http://json-schema.org/draft-07/schema#",
    "title": "TasksPrivateData",
    "type": "array",
    "items": {
      "type": "object",
      "properties": {
        "_id": {
          "type": "string",
          "format": "uuid",
          "coerce": true
        },
        "data": {
          "type": "object",
          "properties": {
            "%share": {
              "type": "string"
            }
          },
          "required": [
            "%share"
          ]
        }
      },
      "required": [
        "_id",
        "data"
      ]
    }
  },
  'TasksPrivateData'
);
console.log('ðŸ“š New Schema:', newSchema);

return newSchema.map(data => ({ json: data }));
